class Cast {
  
  List<Actor> actores = [];

  Cast.fromJsonList(List<dynamic> jsonList) {

    if(jsonList == null) return;

    actores.addAll(jsonList.map((item) => Actor.fromJsonMap(item)));

    // jsonList.forEach((item) { 
      
